sp_widget.script:
(function() {
    data.borrowedBooks = [];
    data.returnedBooks = [];
    data.overdueBooks = [];

    var borrowedBooksUtils = new BorrowedBooksUtils();

    // Fetch borrowed books
    data.borrowedBooks = borrowedBooksUtils.getBorrowedBooks();

    // Fetch returned books
    data.returnedBooks = borrowedBooksUtils.getReturnedBooks();

    // Fetch overdue books
    data.overdueBooks = borrowedBooksUtils.getOverdueBooks();

    // Function to format dates for display (optional)
    function formatDate(dateString) {
        var date = new GlideDateTime(dateString);
        return date.getDisplayValue();
    }

    // Format dates for display in the UI
    data.borrowedBooks.forEach(function(book) {
        book.borrowDate = formatDate(book.borrowDate);
        book.dueDate = formatDate(book.dueDate);
        book.returnedDate = formatDate(book.returnedDate);
    });

    data.returnedBooks.forEach(function(book) {
        book.borrowDate = formatDate(book.borrowDate);
        book.dueDate = formatDate(book.dueDate);
        book.returnedDate = formatDate(book.returnedDate);
    });

    data.overdueBooks.forEach(function(book) {
        book.borrowDate = formatDate(book.borrowDate);
        book.dueDate = formatDate(book.dueDate);
        book.returnedDate = formatDate(book.returnedDate);
    });

})();
