sp_widget.script:
var BorrowedBooksUtil = Class.create();
BorrowedBooksUtil.prototype = {
    initialize: function() {},

    getUserBorrowedBooks: function() {
        var userId = gs.getUserID(); // Get the current user's ID

        // Query the Borrow table for books borrowed by the current user
        var borrowedBooks = [];
        var gr = new GlideRecord('x_1383151_bkquest_borrow'); // Replace with your actual table name
        gr.addQuery('user_id', userId);
        gr.query();
        while (gr.next()) {
            var book = {
                bookId: gr.getValue('book_id'),
                title: gr.getValue('title'),
                borrowDate: gr.getValue('borrow_date'),
                dueDate: gr.getValue('due_date'),
                // Add more fields as needed
            };
            borrowedBooks.push(book);
        }

        return JSON.stringify(borrowedBooks); // Return as JSON string
    },

    type: 'BorrowedBooksUtil'
};
