sp_widget.client_script:
api.controller = function($location, $scope, spUtil) {
	var c = this;

	c.borrowBook = function() {
		c.server.get({
			action: 'borrowBook',
			bookSysId: c.data.book.sys_id
		}).then(function(response) {
			if (response.success) {
				spUtil.addInfoMessage('Book borrowed successfully!');
			} else {
				spUtil.addErrorMessage(response.message || 'No available books to borrow.');
			}
		});
	};

};

