sp_widget.css:
.container {
    position: relative; /* Ensures the container can have absolutely positioned children */
    display: flex; /* Enables Flexbox layout */
    justify-content: space-between; /* Adds space between each stat-box */
    align-items: center; /* Vertically centers the boxes */
    padding: 20px; /* Add some padding if needed */
    height: 300px; /* Set the desired height for the container */
    width: 100%; /* Ensure the container takes full width */
    box-sizing: border-box; /* Include padding in height/width calculations */
}

.background-image {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    object-fit: cover; /* Ensures the image covers the entire container */
    z-index: -1; /* Places the image behind the other content */
}

.stat-box {
    position: relative;
    margin: 10px;
    padding: 20px;
    background: rgba(255, 255, 255, 0.8); /* Semi-transparent background for readability */
    border-radius: 5px; /* Optional: to make the corners rounded */
    flex: 1; /* Allows the stat boxes to grow and shrink equally */
    max-width: 200px; /* Optional: set a maximum width for the boxes */
    text-align: center; /* Centers the text inside the boxes */
}

h1, h3 {
    color: black;
}

sp_widget.script:
(function() {
    var counts = new BookQuestCounts().getCounts();
    data.totalBooks = counts.totalBooks;
    data.totalLanguages = counts.totalLanguages;
    data.totalGenres = counts.totalGenres;
    data.totalBorrowedBooks = counts.totalBorrowedBooks;
})();
