sp_widget.css:
.container {
    display: flex;
    justify-content: space-between;
    padding: 20px;
}

.stat-box {
    flex: 1;
    margin: 0 10px;
    padding: 20px;
    text-align: center;
    border: 1px solid #ccc;
    border-radius: 8px;
    background-color: #f9f9f9;
}

.stat-box h2 {
    margin: 0 0 10px;
    font-size: 24px;
}

.stat-box p {
    margin: 0;
    font-size: 18px;
}

sp_widget.script:
(function() {
    var data = {};

    // Initialize an array to store the statistics
    data.stats = [];

    // Count Total Books
    var bookGR = new GlideRecord('x_1383151_bkquest_book');
    bookGR.query();
    var totalBooks = bookGR.getRowCount();

    // Count Total Genres
    var genreGR = new GlideRecord('x_1383151_bkquest_genre_lookup');
    genreGR.query();
    var totalGenres = genreGR.getRowCount();

    // Count Total Borrowed Books
    var borrowGR = new GlideRecord('x_1383151_bkquest_borrow');
    borrowGR.query();
    var totalBorrowedBooks = borrowGR.getRowCount();

    // Push the counts into the stats array
    data.stats.push({
        totalBooks: totalBooks,
        totalGenres: totalGenres,
        totalBorrowedBooks: totalBorrowedBooks
    });

    // Return the data object to the client-side
    return data;
})();
