sp_widget.client_script:
function($scope, $http) {
	var c = this;
	c.data = {
		books: []
	};
	c.searchQuery = '';

	// Load all books initially
	$http.get('/api/x_1383151_bkquest/book_api') // Replace with your actual API endpoint
		.then(function(response) {
		console.log('Books data fetched successfully:', response.data.result);
		c.data.books = response.data.result;
	}, function(error) {
		console.error('Error fetching book data:', error);
	});

	// Function to filter books based on search query
	c.filteredBooks = function() {
		if (!c.searchQuery) {
			return c.data.books;
		}
		return c.data.books.filter(function(book) {
			return book.title.toLowerCase().includes(c.searchQuery.toLowerCase());
		});
	};
}

