sp_widget.script:
(function() {
    var borrowSysId = $sp.getParameter('borrow_sys_id'); // Assuming you pass the borrow_sys_id parameter to fetch specific borrow details

		var result = {};
    if (input && input.action === 'fetchBorrowDetails') {
        result = fetchBorrowDetails(input.borrowSysId);
    }
    // Function to fetch borrow details
    function fetchBorrowDetails(borrowSysId) {
        var borrowGR = new GlideRecord('x_1383151_bkquest_borrow');
        if (borrowGR.get(borrowSysId)) {
            var bookInstanceId = borrowGR.book_id;

            // Check if the book is reserved
            if (isBookReserved(bookInstanceId)) {
                return { error: 'Book is reserved and cannot be extended.' };
            }

            // Extend borrow period (Example: increase by 7 days)
            var newborrowPeriod = new GlideDateTime(borrowGR.days);
            newborrowPeriod.addDays(14); // Extend by 7 days, modify as needed

            borrowGR.days = newborrowPeriod;
            borrowGR.update();

            return {
                success: true,
                message: 'Borrow extended successfully.',
                borrowDetails: {
										// borrowSysId: borrowGR.sys_id.toString(),
                    borrowId: borrowGR.number.toString(),
										borrowPeriod: borrowGR.days.toSyring(),
                    borrowDate: borrowGR.borrow_date.toString(),
                    dueDate: borrowGR.due_date.toString(),
                    returnedDate: borrowGR.returned_date.toString(),
                    daysLate: borrowGR.days_late.toString(),
                    fineAmount: borrowGR.fine_amount.toString(),
                    finePaidDate: borrowGR.fine_paid_date.toString(),
                    bookInstanceId: bookInstanceId,
                    userId: borrowGR.user_id.toString()
                }
            };
        } else {
            return { error: 'Borrow record not found.' };
        }
    }

    // Function to check if a book instance is reserved
    function isBookReserved(bookInstanceId) {
        var reservationGR = new GlideRecord('x_1383151_bkquest_reservation');
        reservationGR.addQuery('book_id', bookInstanceId);
        reservationGR.query();
        return reservationGR.hasNext();
    }

    // Handle API request
    /*var result = {};
    if (input && input.action === 'fetchBorrowDetails') {
        result = fetchBorrowDetails(input.borrowSysId);
    }*/

    data.response = result;

})();
