sp_widget.client_script:
api.controller = function($scope, spUtil, $http) {
    // Initialize variables
    $scope.borrowedBookDetails = {};

    // Function to fetch borrow details based on sys_id
    $scope.fetchBorrowDetails = function(sysId) {
        // Make a server call to fetch borrow details
        var url = '/api/now/sp/u_borrow_details_by_sys_id?sys_id=' + sysId;
        $http.get(url).then(function(response) {
            // Handle successful response
            $scope.borrowedBookDetails = response.data;
        }, function(error) {
            // Handle error
            console.error('Error fetching borrow details:', error);
        });
    };

    // Function to initialize page and fetch borrow details based on URL parameter
    $scope.initPage = function() {
        // Retrieve the sys_id parameter from the URL
        var sysId = spUtil.getURLParameter('sys_id');

        // Fetch borrow details if sysId is present
        if (sysId) {
            $scope.fetchBorrowDetails(sysId);
        } else {
            console.error('sys_id parameter not found in URL.');
        }
    };

    // Call initPage function when the controller loads
    $scope.initPage();
}

