sp_widget.script:
(function() {
    // Retrieve the sys_id parameter from the URL
var sysId = $sp.getParameter('sys_id');

// Create an instance of the BorrowedBooksUtils script include
var borrowedUtils = new BorrowedBooksUtils();

// Fetch borrow details for the specified sys_id
var borrowedBookDetails = borrowedUtils.getBorrowDetailsBySysId(sysId);

data.borrowedBookDetails = borrowedBookDetails;

})();
