sp_widget.script:
(function() {
    // Retrieve the borrow_sys_id parameter from the request
    var borrowSysId = $sp.getParameter('sys_id'); 
    console.log(borrowSysId);

    // Instantiate the BorrowedBooksUtils class
    var borrowedBooksUtils = new BorrowedBooksUtils();

    // Call the getBorrowedBooks method with the borrowSysId parameter
    var borrowedBooks = borrowedBooksUtils.getBorrowedBooks(borrowSysId);

    // Attach the filtered result to the data object to pass it to the client-side script
    data.borrowedBooks = borrowedBooks;
})();
