sp_widget.script:
(function(){
    var userSysId = gs.getUserID();
    var gr = new GlideRecord('x_1383151_bkquest_borrow');
    gr.addQuery('user_id', userSysId);
    gr.query();
    var array = [];
    while (gr.next()) {
        var out = {};

        // Get the book copy record
        var bookCopyGR = new GlideRecord('x_1383151_bkquest_book_copy');
        if (bookCopyGR.get(gr.book_copy)) {
            out.title = bookCopyGR.getDisplayValue('title');
            out.author = bookCopyGR.getDisplayValue('author');
        }

        out.borrow_date = gr.getDisplayValue('borrow_date');
        out.due_date = gr.getDisplayValue('due_date');

        array.push(out);
    }
    data.array = array;
})();
