sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    // Get the sys ID of the new book instance
    var sysID = current.title; //title is Book ISBN (Changed field name)

    // Get the Book Definition record for this ISBN
    var bookDef = new GlideRecord('x_1383151_bkquest_book');
	bookDef.addQuery('sys_id', sysID);
	bookDef.query();

    // Check if the Book Definition record exists
    if (!bookDef) {
        // Handle error: Book not found in Book Definition table
        gs.error('Book with ISBN not found.');
        current.setAbortAction(true); // Abort insert operation
        return;
    }

    // Get the Quantity from the Book Definition record
    //var quantity = bookDef.addQuery();

    // Get the current number of Book Instance records for this ISBN
    var count = new GlideAggregate('x_1383151_bkquest_book_copy');
    count.addQuery('isbn', current.isbn);
    count.addAggregate('COUNT');
    count.query();

    // Check if adding this instance would exceed the Quantity
    if (count + 1 > quantity) {
        // Error: Adding this instance would violate the limit
        gs.error('Cannot add more copies. ISBN: ' + isbn + ' has only ' + quantity + ' available in stock.');
        current.setAbortAction(true); // Abort insert operation
    }

})(current, previous);
