sys_script.script:
(function executeRule(current, previous /* , gs */ ) {
    // Check if Borrow Status has changed
    if (current.borrow_status.changes()) {
        var days = current.days; 

        //Get maxBorrowDays from a property
        var maxBorrowDays = gs.getProperty('x_1383151_bkquest.max_borrow_days', 28); // default value

        // Validate the borrow period
        if (days > parseInt(maxBorrowDays, 10)) {
            // Get a localized message for the error
            var errorMessage = gs.getMessage('The borrow period cannot exceed {0} days.', maxBorrowDays);
        }

        // Check if Borrow Status has changed to 'extend'
        if (current.borrow_status == 'extend') {
            var extraDays = gs.getProperty('x_1383151_bkquest.extend_borrow_period', 14); // Default to 14 days if property is not set
            current.days += parseInt(extraDays, 10);
        }

        // Check if Borrow Status has changed to 'return'
        if (current.borrow_status == 'return') {
            var returnedDate = new GlideDate();
            returnedDate.setValue(new GlideDateTime()); // Set returned date to current date
            current.returned_date = returnedDate;
        }
    }

})(current, previous);
