sys_script.script:
(function executeRule(current, previous /*null when async*/) {
    // Check if the Borrow Status is being updated to "Extend"
    if (current.borrow_status == 'extend' && previous.borrow_status != 'extend') {
        // Check if the book has already been extended once
        if (current.extension_count > 0) {
            // Book has already been extended once, prevent further extension
            gs.addErrorMessage('You can only extend this book once.');
            current.borrow_status = previous.borrow_status; // Rollback to previous status
            return;
        }
        // Increment the extension count
        current.extension_count = 1;

        // Set the returned_date to today's date when extending
        current.returned_date = new GlideDateTime(); // or gs.nowDateTime();

        // Hide the "Extend" option from the choice list
        current.setWorkflow(false); // Disable workflow to bypass validation
        g_form.removeOption('borrow_status', 'extend'); // Remove the "Extend" option
        current.setWorkflow(true); // Re-enable workflow
    }
})(current, previous);
