sys_script.script:
(function executeRule(current, previous) {
    // Check if the book has been returned (i.e., returned_date is not empty)
    if (current.returned_date && current.due_date) {
        // Retrieve days late from the days_late field
        var daysLate = current.days_late;

        // Check if days late is positive (book is returned late)
        if (daysLate > 0) {
            // Retrieve fine rate per day from system property
            var fineRatePerDay = gs.getProperty('x_1383151_bkquest.bookquest.fine_rate_per_day');

            if (fineRatePerDay) {
                fineRatePerDay = parseInt(fineRatePerDay, 10); // Parse as integer if not null

                // Calculate fine amount (ensure it's treated as an integer)
                var fineAmount = Math.floor(daysLate * fineRatePerDay); // Use Math.floor to round down if needed

                // Update fine_amount field in the current record
                current.x_1383151_bkquest_borrow.fine_amount = fineAmount;
                current.x_1383151_bkquest_borrow.update(); // Save the record with calculated fine_amount
            } else {
                gs.addErrorMessage('Fine rate per day system property is not configured.');
            }
        }
    }
})(current, previous);
