sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {
    // Check if the days_late field is greater than 0
    if (current.days_late > 0) {
        // Retrieve the fine rate per day from system properties
        var fineRatePerDayStr = gs.getProperty('x_1383151_bkquest.fine_rate_per_day');

        // Check if the property exists
        if (fineRatePerDayStr) {
            // Parse the fine rate per day string to integer
            var fineRatePerDay = parseInt(fineRatePerDayStr);

            // Calculate the fine amount
            var fineAmount = current.days_late * fineRatePerDay;

            // Update the fine_amount field in the current record
            current.fine_amount = fineAmount;
        } else {
            // If the property doesn't exist, use a default value
            var defaultFineRatePerDay = 5; // Default fine rate per day is 5

            // Calculate the fine amount using the default value
            fineAmount = current.days_late * defaultFineRatePerDay;

            // Update the fine_amount field in the current record
            current.fine_amount = fineAmount;

            // Optionally, you can log a message indicating the use of the default value
            gs.log('Using default fine rate per day: ' + defaultFineRatePerDay);
        }
    }
})(current, previous);
