sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

   // Get the current values of first and last name fields
    var firstName = g_form.getValue('first_name');
    var lastName = g_form.getValue('last_name');

    // Check if either name is empty
    if (!firstName || !lastName) return; // Exit if either name is empty

    // Concatenate the names with a space
    var fullName = firstName + ' ' + lastName;

    // Set the value of the author's full name field
    g_form.setValue('full_name', fullName);

}
