sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    var isNewRecord = g_form.isNewRecord();

    var currentDate = new Date();
    var returnedDate = g_form.getValue('returned_date');

    // Convert returned date to Date object
    var returnedDateObj = new Date(returnedDate);

    if (returnedDateObj.toDateString() !== currentDate.toDateString()) {
        // Clear the returned_date field and display error message if it's not today's date
        g_form.clearValue('returned_date');
        g_form.showFieldMsg('returned_date', 'Returned date must be today', 'info');
        return;
    }

    // Clear any existing error message
    g_form.clearMessages('returned_date');

}
