sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    // Function to validate ISBN format
    function validateISBN(isbn) {
        var regex = /^ISBN\d{13}$/;
        return regex.test(isbn.toUpperCase());
    }

    // Validate the ISBN entered by the user
    var isValid = validateISBN(newValue);
    if (!isValid) {
        g_form.showFieldMsg('isbn','Invalid ISBN format. Please enter "ISBN" followed by the 13 digit ISBN number.', 'error');
    }
	else {
		g_form.hideFieldMsg('isbn');
	}

}
