sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    var isNewRecord = g_form.isNewRecord();

    var currentDate = new Date();
    var finePaidDate = g_form.getValue('fine_paid_date');

    // Convert field value to Date object
    var finePaidDateObj = new Date(finePaidDate);

    if (finePaidDateObj.toDateString() !== currentDate.toDateString()) {
        // Clear the field and display info message if it's not today's date
        g_form.clearValue('fine_paid_date');
        g_form.showFieldMsg('fine_paid_date', 'Fine paid date must be today', 'info');
        return;
    }

    // Clear any existing error message
    g_form.clearMessages('fine_paid_date');

}
