sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue === '') {
        return;
    }

    // Check if 'extend' option is selected
    if (newValue === 'extend') {
        // Show message below the field
        var message = 'You can extend this book only once.';
        g_form.showFieldMsg('borrow_status', message, 'info');
    } else {
        // Clear the message if another option is selected
        g_form.clearMessages('borrow_status');
    }
}
