sys_script_client.script:
function onSubmit() {

    // Function to validate ISBN format
    function validateISBN(isbn) {
        var regex = /^ISBN\d{13}$/;
        return regex.test(isbn.toUpperCase());
    }

    // Get the ISBN field value
    var isbnField = g_form.getValue('isbn');

    // Validate the ISBN
    if (!validateISBN(isbnField)) {
        g_form.addErrorMessage("Invalid ISBN format. Please enter a valid ISBN number");
		return false; // Prevent form submission
    }

    // Return true to allow form submission if ISBN is valid
    return true;
}
