sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '') {
		return;
	}

	var fieldName = g_form.getControl('cover_image');
	var fileInput = fieldName.getElementsByTagName('input')[0];

	if (fileInput.files && fileInput.files[0]) {
		var file = fileInput.files[0];
		var img = new Image();

		img.onload = function () {
			var width = img.width;
			var height = img.height;

			// Check if image dimensions are as required (200x300 pixels)
			if (width !== 200 || height !== 300) {
				g_form.showFieldMsg('cover_image', 'Image must be 200x300 pixels.', 'error');
				g_form.clearValue('cover_image'); // Clear the field if validation fails
			} else {
				g_form.hideFieldMsg('cover_image');
			}
		};

		var reader = new FileReader();
		reader.onload = function (e) {
			img.src = e.target.result;
		};

		reader.readAsDataURL(file);
	}

}
