sys_script_include.script:
var BookAvailability = Class.create();
BookAvailability.prototype = {
    initialize: function() {
    },

    isBookAvailable: function(bookDefSysId) {
        var bookInstanceGR = new GlideRecord('x_1383151_bkquest_book_copy');
        bookInstanceGR.addQuery('title', bookDefSysId);
        bookInstanceGR.addQuery('status', 'available');
        bookInstanceGR.query();
        return bookInstanceGR.hasNext();
    },

	//R: Function to get all bookinstances which are available using bookdef

	getAvailableBookIds: function(bookDefSysId) {
        var bookInstanceGR = new GlideRecord('x_1383151_bkquest_book_copy');
        bookInstanceGR.addQuery('title', bookDefSysId);
        bookInstanceGR.addQuery('status', 'available');
        bookInstanceGR.query();

        var bookIds = [];
        while (bookInstanceGR.next()) {
            bookIds.push(bookInstanceGR.getValue('sys_id'));
        }
        return bookIds;
    },

    type: 'BookAvailability'
};
