sys_script_include.script:
var AvailableBooksService = Class.create();
AvailableBooksService.prototype = {
    initialize: function() {},

    getAvailableBooks: function(callback) {
        // Your logic to fetch available books goes here
        var gr = new GlideRecord('x_1383151_bkquest_book_copy');
        gr.addQuery('status', 'available'); // Filter by available books
        gr.query();

        var availableBooks = [];

        while (gr.next()) {
            // Fetch book details and push them to availableBooks array
            availableBooks.push({
                title: gr.getValue('title')
                // Add other book details here
            });
        }

        // Execute the callback function with available books array
        callback(availableBooks);
    },

    type: 'AvailableBooksService'
};
