sys_script_include.script:
var BookService = Class.create();
BookService.prototype = {
    initialize: function() {},

    getBooks: function() {
        var books = new global.GlideQuery('x_1383151_bkquest_book')
            .select(['cover_image', 'author$DISPLAY', 'title'])
            .map(function(book) {
                return {
                    cover_image: book.cover_image ? '/sys_attachment.do?sys_id=' + book.cover_image : '',
                    author: book.author$DISPLAY,
                    title: book.title
                };
            })
            .reduce(function(arr, book) {
                arr.push(book);
                return arr;
            }, []);

        return books;
    },

    type: 'BookService'
};
