sys_script_include.script:
var BookFineCalculator = Class.create();
BookFineCalculator.prototype = {
    initialize: function() {},

    calculateFines: function() {
        // Retrieve the fine rate from system property, default to 1 if not set
        var fineRate = gs.getProperty('x_1383151_bkquest.fine_rate_per_day', '1');
        fineRate = parseFloat(fineRate);

        var fine = new GlideRecord('x_1383151_bkquest_borrow');
        var currentDate = new GlideDateTime(); // Get the current date and time

        fine.addQuery('due_date', '<', currentDate); // Find records where the due date is in the past
        fine.addQuery('fine_status', 'not paid'); // Only process fines that are not paid
        fine.addNullQuery('returned_date'); // Ensure the book has not been returned
        fine.query();

        while (fine.next()) {
            var dueDate = new GlideDateTime(fine.due_date);


            // Calculate the number of days overdue
            var overdueDays = Math.floor((currentDate.getNumericValue() - dueDate.getNumericValue()) / (1000 * 60 * 60 * 24));


            if (overdueDays > 0) {
                var fineAmount = (overdueDays * fineRate).toFixed(2); // Fine per overdue day, rounded to two decimal places
                fine.days_late = overdueDays;
                fine.fine_amount = fineAmount;
                fine.update();

            }
        }

    },

    type: 'BookFineCalculator'
};
