sys_script_include.script:
var BookReservationUtils = Class.create();
BookReservationUtils.prototype = {
    initialize: function() {},

    getReservableBooksSysIds: function() {
        var sysIds = [];

        // Step 1: Collect sys_ids of books with no available instances
        var allBooksGr = new GlideRecord('x_1383151_bkquest_book');
        allBooksGr.query();
        var allBooks = [];
        while (allBooksGr.next()) {
            allBooks.push(allBooksGr.sys_id.toString());
        }

        var availableInstanceGr = new GlideRecord('x_1383151_bkquest_book_copy');
        availableInstanceGr.addQuery('status', 'available');
        availableInstanceGr.query();
        var availableBooks = [];
        while (availableInstanceGr.next()) {
            var bookDefGr = new GlideRecord('x_1383151_bkquest_book');
            if (bookDefGr.get(availableInstanceGr.getValue('title'))) {
                availableBooks.push(bookDefGr.sys_id.toString());
            }
        }

        // Find sys_ids of books with no available instances
        sysIds = allBooks.filter(function(sysId) {
            return !availableBooks.includes(sysId);
        });

        // Return an array of sys_ids
        var result = 'sys_idIN' + sysIds.join(",");
		return result;
    },

    type: 'BookReservationUtils'
};
