sys_script_include.script:
var BookQuestCounts = Class.create();
BookQuestCounts.prototype = {
    initialize: function() {},

    getCounts: function() {
        var result = {};

        // Count Total Books
        var bookGR = new GlideRecord('x_1383151_bkquest_book');
        bookGR.query();
        result.totalBooks = bookGR.getRowCount();

        // Count Unique Languages
        var languageGR = new GlideAggregate('x_1383151_bkquest_book');
        languageGR.addAggregate('COUNT');
        languageGR.groupBy('language');
        languageGR.query();
        result.totalLanguages = 0;
        while (languageGR.next()) {
            result.totalLanguages++;
        }

        // Count Unique Genres
        var genreGR = new GlideAggregate('x_1383151_bkquest_book');
        genreGR.addAggregate('COUNT');
        genreGR.groupBy('genre');
        genreGR.query();
        result.totalGenres = 0;
        while (genreGR.next()) {
            result.totalGenres++;
        }

        // Count Borrowed Books
        var borrowGR = new GlideRecord('x_1383151_bkquest_borrow');
        borrowGR.query();
        result.totalBorrowedBooks = borrowGR.getRowCount();

        return result;
    },

    type: 'BookQuestCounts'
};
