sys_script_include.script:
var BookQuestStats = Class.create();
BookQuestStats.prototype = {
    initialize: function() {},

    getStats: function() {
        var stats = {};

        // Count Total Books
        var bookGR = new GlideRecord('x_1383151_bkquest_book');
        bookGR.query();
        stats.totalBooks = bookGR.getRowCount();

        // Count Unique Languages
        var languageGR = new GlideAggregate('x_1383151_bkquest_book');
        languageGR.addAggregate('COUNT');
        languageGR.groupBy('language');
        languageGR.query();
        stats.totalLanguages = 0;
        while (languageGR.next()) {
            stats.totalLanguages++;
        }

        // Count Total Genres in Genre Lookup Table
        var genreLookupGR = new GlideRecord('x_1383151_bkquest_genre_lookup');
        genreLookupGR.query();
        stats.totalGenres = genreLookupGR.getRowCount();

        // Count Borrowed Books
        var borrowGR = new GlideRecord('x_1383151_bkquest_borrow');
        borrowGR.query();
        stats.totalBorrowedBooks = borrowGR.getRowCount();

        return stats;
    },

    type: 'BookQuestStats'
};
