sys_script_include.script:
var BorrowedReservationUtils = Class.create();
BorrowedReservationUtils.prototype = Object.extendsObject(AbstractAjaxProcessor, {

    // Function to check if a borrowed book is reserved
    isBorrowedBookReserved: function(borrowSysId) {
        var reserved = false;

        // Get the book ID from the Borrow table
        var borrowGr = new GlideRecord('x_1383151_bkquest_borrow');
        if (borrowGr.get(borrowSysId)) {
            var bookId = borrowGr.book_id.toString();

            // Query Book Instance to get the ISBN
            var instanceGr = new GlideRecord('x_1383151_bkquest_book_copy');
            if (instanceGr.get('book_id', bookId)) {
                var isbn = instanceGr.isbn;

                // Query Reservation to check if the book is reserved
                var reservationGr = new GlideRecord('x_1383151_bkquest_reservation');
                reservationGr.addQuery('book_isbn', isbn);
                reservationGr.query();
                if (reservationGr.next()) {
                    reserved = true;
                }
            }
        }

        return reserved;
    },

    type: 'BorrowedReservationUtils'
});
