sys_ui_policy.script_true:
function onCondition() {
    // Check if the record is new
    if (g_form.isNewRecord()) {
        // The record is new, ensure the days field is editable
        g_form.setReadOnly('days', false);
    } else {
        // The record is not new, check if the days field has a value
        var daysValue = g_form.getValue('days');
        if (daysValue) {
            // If days field has a value, make it read-only
            g_form.setReadOnly('days', true);
        } else {
            // If days field does not have a value, make it editable
            g_form.setReadOnly('days', false);
        }
    }
}
