sys_ui_script.script:
var x_1383151_bkquest = x_1383151_bkquest || {};

x_1383151_bkquest.BookAvailabilityAjax = (function() {
    "use strict";

    return {
        isBookAvailable: function(bookDefSysId, callback) {
            var ga = new GlideAjax('BookAvailabilityAjax');
            ga.addParam('sysparm_name', 'isBookAvailable');
            ga.addParam('sys_id', bookDefSysId);
            ga.getXMLAnswer(function(response) {
                var isAvailable = response === 'true';
                if (typeof callback === 'function') {
                    callback(isAvailable);
                }
            });
        },

        type: "BookAvailabilityAjax"
    };
})();
